import "./App.css";
import { useState } from "react";
import foodData from "src/food-data.json";
import FoodItem from "src/components/FoodItem.js";

/* ####### DO NOT TOUCH -- this makes the image URLs work ####### */
foodData.forEach((item) => {
  item.image = process.env.PUBLIC_URL + "/" + item.image;
});
/* ############################################################## */

function App() {
  // TODO: use useState to create a state variable to hold the state of the cart
  /* add your cart state code here */



  const [items, setItem] = useState([]);
  const [total, setTotal] = useState(0);

  const addToCart = (itemName, price) => {
    setItem([...items, itemName]);
    setTotal(total+price);
    // items.push(itemName)
  }

  // function handleClick(newItem) {
  //     setItem(items.push(newItem));
  // }

  return (

    <div className="App">
      <h1>My Bakery</h1>

      {foodData.map((item, index) => (
        <FoodItem prop1={item} prop2={index} updateClick={addToCart}/>
      ))}

      <div>
        <h2>Cart</h2>
        {/* {items.map((item) => {
          return <p>{item.name}</p>
        })} */}

        {items}
        <h2>Total cost: {total} </h2> 
      </div>
    </div>
  );
}

export default App;
