import "./App.css";
import { useState } from "react";
import foodData from "./food-data.json";
import FoodItem from "./components/FoodItem.js";

/* ####### DO NOT TOUCH -- this makes the image URLs work ####### */
foodData.forEach((item) => {
  item.image = process.env.PUBLIC_URL + "/" + item.image;
});
/* ############################################################## */

function App() {

  const [items, setItem] = useState([]);
  const [total, setTotal] = useState(0);

  const addToCart = (itemName, price) => {
    setItem([...items, itemName]);
    setTotal(total+price);
  }

  return (
    <div className="App">

      <div className="header" class="d-flex flex-row" >
        <img src="images/logo2.jpg" class="rounded-circle" height="290" padding="100" alt="logo displaying the royal palace at Mandalay in black and white sketch"></img>
        {/* class="img-fluid" */}
        <h1>Mandalay</h1>
      </div>

      {foodData.map((item, index) => (
        <FoodItem prop1={item} prop2={index} updateClick={addToCart}/>
      ))}

      <div>
        <h2>Cart</h2>
        {/* {items.map((item) => {
          return <p>{item.name}</p>
        })} */}

        {items}
        <h2>Total cost: {total} </h2> 
      </div>
    </div>
  );
}

export default App;
